/**
 * PetCareApp - AdminContent - ZarzƒÖdzanie tre≈õciƒÖ
 * @author VS
 */
import React, { useState, useEffect } from 'react';
import { useTranslation } from 'react-i18next';
import DashboardLayout from '../../components/common/DashboardLayout';
import Card from '../../components/common/Card';
import Button from '../../components/common/Button';
import Input from '../../components/common/Input';
import LoadingSpinner from '../../components/common/LoadingSpinner';
import { useNotification } from '../../context/NotificationContext';
import '../dashboards/DashboardPages.css';
import './AdminPages.css';

function AdminContent() {
    const { t } = useTranslation();
    const { showNotification } = useNotification();
    const [contents, setContents] = useState([]);
    const [loading, setLoading] = useState(true);
    const [showModal, setShowModal] = useState(false);
    const [formData, setFormData] = useState({ title: '', type: 'news', excerpt: '', content: '', published: true });

    const menuItems = [
        { path: '/dashboard/admin', label: t('dashboard.admin.overview'), icon: 'üìä', exact: true },
        { path: '/dashboard/admin/users', label: t('dashboard.admin.users'), icon: 'üë•' },
        { path: '/dashboard/admin/appointments', label: t('dashboard.admin.appointments'), icon: 'üìÖ' },
        { path: '/dashboard/admin/services', label: t('dashboard.admin.services'), icon: 'üíº' },
        { path: '/dashboard/admin/content', label: t('dashboard.admin.content'), icon: 'üìù' },
        { path: '/dashboard/admin/reports', label: t('dashboard.admin.reports'), icon: 'üìà' }
    ];

    useEffect(() => {
        setLoading(true);
        setTimeout(() => {
            setContents([
                { id: '1', title: 'Nowe godziny otwarcia w okresie ≈õwiƒÖtecznym', type: 'news', excerpt: 'Informujemy o zmianach w godzinach pracy...', date: '2024-12-15', published: true, icon: 'üì¢' },
                { id: '2', title: 'Jak przygotowaƒá psa do wizyty u weterynarza', type: 'article', excerpt: 'Porady dla w≈Ça≈õcicieli przed wizytƒÖ...', date: '2024-12-10', published: true, icon: 'üìÑ' },
                { id: '3', title: 'Promocja na szczepienia', type: 'promotion', excerpt: '-20% na wszystkie szczepienia do ko≈Ñca roku', date: '2024-12-01', published: true, icon: 'üéÅ' },
                { id: '4', title: 'Nowy weterynarz w zespole', type: 'news', excerpt: 'Z rado≈õciƒÖ witamy dr Mariƒô Wi≈õniewskƒÖ...', date: '2024-11-20', published: true, icon: 'üì¢' }
            ]);
            setLoading(false);
        }, 500);
    }, []);

    const handleSave = () => {
        if (!formData.title) { showNotification('Podaj tytu≈Ç', 'error'); return; }
        const icons = { news: 'üì¢', article: 'üìÑ', promotion: 'üéÅ', faq: '‚ùì' };
        setContents(prev => [...prev, { id: Date.now().toString(), ...formData, date: new Date().toISOString().split('T')[0], icon: icons[formData.type] }]);
        showNotification('Tre≈õƒá dodana', 'success');
        setShowModal(false);
        setFormData({ title: '', type: 'news', excerpt: '', content: '', published: true });
    };

    const togglePublished = (id) => setContents(prev => prev.map(c => c.id === id ? { ...c, published: !c.published } : c));
    const deleteContent = (id) => { setContents(prev => prev.filter(c => c.id !== id)); showNotification('Usuniƒôto', 'success'); };

    if (loading) return <DashboardLayout menuItems={menuItems} title="Tre≈õci" roleColor="#9b59b6"><LoadingSpinner /></DashboardLayout>;

    return (
        <DashboardLayout menuItems={menuItems} title="ZarzƒÖdzanie tre≈õciƒÖ" roleColor="#9b59b6">
            <div className="dashboard-page">
                <div className="page-header">
                    <div className="filter-buttons">
                        <button className="filter-btn active">Wszystkie</button>
                        <button className="filter-btn">üì¢ Aktualno≈õci</button>
                        <button className="filter-btn">üìÑ Artyku≈Çy</button>
                        <button className="filter-btn">üéÅ Promocje</button>
                    </div>
                    <Button onClick={() => setShowModal(true)}>+ Dodaj tre≈õƒá</Button>
                </div>

                <Card>
                    <div className="content-list">
                        {contents.map(item => (
                            <div key={item.id} className="content-item" style={{ opacity: item.published ? 1 : 0.6 }}>
                                <div className="content-thumbnail">{item.icon}</div>
                                <div className="content-info">
                                    <div className="content-title">{item.title}</div>
                                    <div className="content-excerpt">{item.excerpt}</div>
                                    <div className="content-meta">{item.date} ‚Ä¢ {item.published ? '‚úÖ Opublikowane' : 'üìù Szkic'}</div>
                                </div>
                                <div style={{ display: 'flex', flexDirection: 'column', gap: 'var(--space-2)' }}>
                                    <Button variant="ghost" size="small" onClick={() => togglePublished(item.id)}>{item.published ? 'üìù' : '‚úÖ'}</Button>
                                    <Button variant="ghost" size="small" onClick={() => deleteContent(item.id)}>üóëÔ∏è</Button>
                                </div>
                            </div>
                        ))}
                    </div>
                </Card>

                {showModal && (
                    <div className="modal-overlay" onClick={() => setShowModal(false)}>
                        <div className="modal-content modal-large" onClick={e => e.stopPropagation()}>
                            <div className="modal-header"><h2>Nowa tre≈õƒá</h2><button className="modal-close" onClick={() => setShowModal(false)}>√ó</button></div>
                            <div className="modal-body">
                                <div className="form-grid">
                                    <Input label="Tytu≈Ç *" value={formData.title} onChange={e => setFormData(p => ({ ...p, title: e.target.value }))} />
                                    <div className="form-group">
                                        <label>Typ</label>
                                        <select value={formData.type} onChange={e => setFormData(p => ({ ...p, type: e.target.value }))}>
                                            <option value="news">üì¢ Aktualno≈õƒá</option>
                                            <option value="article">üìÑ Artyku≈Ç</option>
                                            <option value="promotion">üéÅ Promocja</option>
                                            <option value="faq">‚ùì FAQ</option>
                                        </select>
                                    </div>
                                </div>
                                <div className="form-group" style={{ marginTop: 'var(--space-4)' }}>
                                    <label>Zajawka</label>
                                    <textarea value={formData.excerpt} onChange={e => setFormData(p => ({ ...p, excerpt: e.target.value }))} rows={2} />
                                </div>
                                <div className="form-group">
                                    <label>Tre≈õƒá</label>
                                    <textarea value={formData.content} onChange={e => setFormData(p => ({ ...p, content: e.target.value }))} rows={6} />
                                </div>
                            </div>
                            <div className="modal-footer"><Button variant="ghost" onClick={() => setShowModal(false)}>Anuluj</Button><Button onClick={handleSave}>Publikuj</Button></div>
                        </div>
                    </div>
                )}
            </div>
        </DashboardLayout>
    );
}

export default AdminContent;
